import React, { useState } from 'react';
import './App.css';
import Jogo from './components/Jogo';
import Footer from './components/Footer';
import { useTheme } from './context/ThemeContext';
import { Tooltip } from 'react-tooltip';

function App() {
    const { theme } = useTheme();
    const [modoDeJogo, setModoDeJogo] = useState('menu');

    if (modoDeJogo === 'menu') {
        return (
            <div className={`App ${theme}`}>
                <h1>Adivinhe o País!</h1>
                <div className="menu-container">
                    <h2>Escolha um modo de jogo</h2>

                    <button 
                        onClick={() => setModoDeJogo('classico')}
                        data-tooltip-id="menu-tooltip" 
                        data-tooltip-content="Faça 5 pontos para vencer, mas cuidado, você só tem 3 vidas!"
                    >
                        Modo Clássico
                    </button>
                    <button 
                        onClick={() => setModoDeJogo('timeAttack')}
                        data-tooltip-id="menu-tooltip" 
                        data-tooltip-content="Faça o máximo de pontos em 60 segundos. Cada acerto te dá +2 segundos e cada erro te tira 10!"
                    >
                        Time Attack
                    </button>
                </div>
                <Footer />

                <Tooltip 
                    id="menu-tooltip" 
                    style={{ 
                        backgroundColor: "#14161aff", 
                        color: "#76e0fdff",
                        borderRadius: "8px",
                        maxWidth: "250px",
                        textAlign: "center"
                    }}
                />
            </div>
        );
    }

    return (
        <div className={`App ${theme}`}>
            <Jogo 
                modo={modoDeJogo} 
                voltarAoMenu={() => setModoDeJogo('menu')} 
            />
            <Footer />
        </div>
    );
}

export default App;
