import React, { useState } from 'react';
import './App.css';
import Jogo from './components/Jogo';
import Footer from './components/Footer';
import { useTheme } from './context/ThemeContext';
import { motion, AnimatePresence } from 'framer-motion'; // Importamos as ferramentas

function App() {
    const { theme } = useTheme();
    const [modoDeJogo, setModoDeJogo] = useState('menu');

    // Configurações da animação de transição de tela
    const pageVariants = {
        initial: { opacity: 0, x: "-100vw" },
        in: { opacity: 1, x: 0 },
        out: { opacity: 0, x: "100vw" }
    };
    const pageTransition = {
        type: "tween",
        ease: "anticipate",
        duration: 0.8
    };

    return (
        <div className={`App ${theme}`}>
            {/* AnimatePresence vai cuidar da transição */}
            <AnimatePresence mode="wait">

                {modoDeJogo === 'menu' ? (
                    <motion.div
                        key="menu"
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                    >
                        <h1>Adivinhe o País!</h1>
                        <div className="menu-container">
                            <h2>Escolha um modo de jogo</h2>
                            <button onClick={() => setModoDeJogo('classico')} title="Faça 5 pontos para vencer...">Modo Clássico</button>
                            <button onClick={() => setModoDeJogo('timeAttack')} title="Faça o máximo de pontos em 60s...">Time Attack</button>
                        </div>
                    </motion.div>
                ) : (
                    <motion.div
                        key="jogo"
                        initial="initial"
                        animate="in"
                        exit="out"
                        variants={pageVariants}
                        transition={pageTransition}
                    >
                        <Jogo modo={modoDeJogo} voltarAoMenu={() => setModoDeJogo('menu')} />
                    </motion.div>
                )}

            </AnimatePresence>
            <Footer />
        </div>
    );
}

export default App;
