import React, { useState, useEffect, useCallback, useRef } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import 'sweetalert2/dist/sweetalert2.min.css';
import styles from './Jogo.module.css';
import { useTheme } from '../context/ThemeContext';
import { motion, AnimatePresence, useAnimationControls } from 'framer-motion';
import TimerBar from './TimerBar'; // Importamos a nossa nova barra de tempo

function Jogo({ modo, voltarAoMenu }) {
    const { theme } = useTheme();
    const [pergunta, setPergunta] = useState(null);
    const [placar, setPlacar] = useState({ score: 0, vidas: 3 });
    const [feedback, setFeedback] = useState('');
    const [carregando, setCarregando] = useState(true);
    const [statusResposta, setStatusResposta] = useState({ selecionada: null, correta: null });
    const [tempo, setTempo] = useState(60);

    const scoreControls = useAnimationControls();
    const vidasControls = useAnimationControls();
    const tempoControls = useAnimationControls(); // Controle para o texto do tempo
    const placarAnterior = useRef(placar);
    const tempoAnterior = useRef(tempo);

    // ... (buscarPergunta não muda)

    useEffect(() => {
        // ... (useEffect de início de jogo não muda)
    }, [modo, buscarPergunta]);

    useEffect(() => {
        if (placar.score > placarAnterior.current.score) {
            scoreControls.start({ scale: [1, 1.4, 1], transition: { duration: 0.4 } });
        }
        if (placar.vidas < placarAnterior.current.vidas) {
            vidasControls.start({ x: [0, -5, 5, -5, 0], transition: { duration: 0.4 } });
        }
        // Anima o bônus de tempo
        if (tempo > tempoAnterior.current) {
            tempoControls.start({
                scale: [1, 1.4, 1],
                color: ['#61dafb', '#28a745', '#61dafb'], // Pisca em verde
                transition: { duration: 0.8 }
            });
        }
        placarAnterior.current = placar;
        tempoAnterior.current = tempo;
    }, [placar, tempo, scoreControls, vidasControls, tempoControls]);

    // ... (useEffect do timer e de fim de jogo não mudam)

    const handleResposta = async (respostaDoUsuario) => { /* ... (esta função não muda) ... */ };
    const getButtonClass = (opcao) => { /* ... (esta função não muda) ... */ };

    if (!pergunta && carregando) { return <div>Carregando...</div>; }
    if (!pergunta) { return <div>Não foi possível carregar o jogo. Verifique se a API está no ar.</div>; }

    return (
        <div className={theme}> 
            <div className={styles.container}>
                <div className={styles.placar}>
                    <motion.p animate={scoreControls}>
                        <b>Pontos: {placar.score}</b>
                    </motion.p>
                    {modo === 'classico' ? (
                        <motion.p animate={vidasControls}>
                            <b>Vidas: {placar.vidas} ❤️</b>
                        </motion.p>
                    ) : (
                        <motion.p animate={tempoControls}>
                            <b>Tempo: {tempo < 0 ? 0 : tempo}s ⏳</b>
                        </motion.p>
                    )}
                </div>

                {/* Mostra a barra de tempo apenas no modo Time Attack */}
                {modo === 'timeAttack' && <TimerBar tempo={tempo} tempoMaximo={60} />}
                
                <AnimatePresence mode="wait">
                    {/* ... (Animação da pergunta não muda) ... */}
                </AnimatePresence>
                
                <AnimatePresence>
                    {/* Animação para a mensagem de feedback */}
                    {feedback && (
                        <motion.div
                            key="feedback"
                            className={styles.feedback}
                            initial={{ opacity: 0, scale: 0.5 }}
                            animate={{ opacity: 1, scale: 1 }}
                            exit={{ opacity: 0, scale: 0.5 }}
                        >
                            {feedback}
                        </motion.div>
                    )}
                </AnimatePresence>

                <div className={styles.opcoes}>
                    {/* ... (Animação dos botões não muda) ... */}
                </div>
                <button onClick={voltarAoMenu} className={styles.menuButton}>Voltar ao Menu</button>
            </div>
        </div>
    );
}

export default Jogo;
