import React from 'react';
import { FaUserGraduate, FaCog, FaTrophy } from 'react-icons/fa';
import Swal from 'sweetalert2';
import styles from './Footer.module.css';
import { useTheme } from '../context/ThemeContext';
// import { useAchievements } from '../context/AchievementContext'; // J√° est√° no c√≥digo, apenas para refer√™ncia

function Footer() {
    const { theme } = useTheme(); // <-- Importante: pegamos o tema aqui!
    // const { conquistas } = useAchievements(); // J√° est√° no c√≥digo, apenas para refer√™ncia

    // Fun√ß√£o auxiliar para obter as classes do SweetAlert2 baseadas no tema
    const getSwalThemeClasses = () => {
        return {
            popup: `meu-modal-popup ${theme === 'dark' ? 'meu-modal-dark' : 'meu-modal-light'}`,
            title: `meu-modal-title ${theme === 'dark' ? 'meu-modal-title-dark' : 'meu-modal-title-light'}`,
            htmlContainer: `meu-modal-content ${theme === 'dark' ? 'meu-modal-content-dark' : 'meu-modal-content-light'}`,
            confirmButton: `meu-modal-confirm-button ${theme === 'dark' ? 'meu-modal-confirm-button-dark' : 'meu-modal-confirm-button-light'}`,
            closeButton: `meu-modal-close-button ${theme === 'dark' ? 'meu-modal-close-button-dark' : 'meu-modal-close-button-light'}`,
            // Adicione outras classes se precisar estilizar mais partes do modal
        };
    };

    const abrirModalCreditos = () => {
        Swal.fire({
            title: 'Cr√©ditos do Projeto',
            html: `
                <div style="text-align: left;"> {/* Removido 'color: white;' daqui */}
                    <p><strong>Desenvolvido por:</strong></p>
                    <p>Mateus Pedrini Wolf</p>
                    <br>
                    <p><strong>APIs Utilizadas:</strong></p>
                    <ul>
                        <li>REST Countries</li>
                        <li>ExchangeRate-API</li>
                    </ul>
                    <br>
                    <p><strong>Tecnologias:</strong></p>
                    <ul>
                        <li>React.js</li>
                        <li>Node.js / Express.js</li>
                    </ul>
                </div>
            `,
            icon: 'info',
            customClass: getSwalThemeClasses() // <-- Usamos a fun√ß√£o aqui
        });
    };

    const abrirModalConquistas = () => {
        // const { conquistas } = useAchievements(); // J√° est√° declarado l√° em cima
        const { conquistas } = useAchievements(); // Precisamos chamar de novo aqui para ter acesso ao estado atualizado

        const conquistasHtml = Object.entries(conquistas).map(([id, conquista]) => `
            <div 
                class="conquista-item ${conquista.desbloqueada ? 'desbloqueada' : ''}" 
                data-tooltip-id="global-tooltip" 
                data-tooltip-content="${conquista.descricao}"
            >
                <div class="conquista-icon">${conquista.desbloqueada ? 'üèÜ' : 'üîí'}</div>
                <div class="conquista-text">
                    <strong>${conquista.titulo}</strong>
                </div>
            </div>
        `).join('');

        Swal.fire({
            title: 'Sala de Trof√©us',
            html: `<div class="conquistas-container">${conquistasHtml}</div>`,
            customClass: getSwalThemeClasses() // <-- Usamos a fun√ß√£o aqui
        });
    };

    const abrirModalConfig = () => {
        Swal.fire({
            title: 'Configura√ß√µes de Tema',
            html: `
                <div class="theme-buttons-container">
                    <button id="light-mode-btn" class="theme-button">Modo Claro ‚òÄÔ∏è</button>
                    <button id="dark-mode-btn" class="theme-button">Modo Escuro üåô</button>
                </div>
            `,
            showConfirmButton: false,
            showCloseButton: true,
            customClass: getSwalThemeClasses(), // <-- Usamos a fun√ß√£o aqui
            didOpen: () => {
                const lightBtn = document.getElementById('light-mode-btn');
                const darkBtn = document.getElementById('dark-mode-btn');

                lightBtn.addEventListener('click', () => {
                    setTheme('light');
                    Swal.close();
                });

                darkBtn.addEventListener('click', () => {
                    setTheme('dark');
                    Swal.close();
                });
            }
        });
    };

    return (
        <footer className={styles.footer}>
            <FaUserGraduate className={styles.footerIcon} onClick={abrirModalCreditos} title="Cr√©ditos" />
            <FaTrophy className={styles.footerIcon} onClick={abrirModalConquistas} title="Conquistas" />
            <FaCog className={styles.footerIcon} onClick={abrirModalConfig} title="Configura√ß√µes" />
        </footer>
    );
}

export default Footer;
