import React, { useState, useEffect, useCallback, useRef } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import 'sweetalert2/dist/sweetalert2.min.css';
import styles from './Jogo.module.css';
import { useTheme } from '../context/ThemeContext';
import { motion, AnimatePresence, useAnimationControls } from 'framer-motion';

function Jogo({ modo, voltarAoMenu }) {
    const { theme } = useTheme();
    const [pergunta, setPergunta] = useState(null);
    const [placar, setPlacar] = useState({ score: 0, vidas: 3 });
    const [feedback, setFeedback] = useState('');
    const [carregando, setCarregando] = useState(true);
    const [statusResposta, setStatusResposta] = useState({ selecionada: null, correta: null });
    const [tempo, setTempo] = useState(60);

    // ---- NOVIDADES PARA ANIMAÇÃO ----
    const scoreControls = useAnimationControls();
    const vidasControls = useAnimationControls();
    const placarAnterior = useRef(placar);

    const buscarPergunta = useCallback(async () => {
        // ... (código da função buscarPergunta não muda)
    }, []);

    useEffect(() => {
        // ... (código do useEffect de início de jogo não muda)
    }, [modo, buscarPergunta]);

    // ---- NOVO useEffect PARA ANIMAR O PLACAR ----
    useEffect(() => {
        // Anima os pontos se eles aumentaram
        if (placar.score > placarAnterior.current.score) {
            scoreControls.start({
                scale: [1, 1.4, 1],
                transition: { duration: 0.4, times: [0, 0.2, 1] }
            });
        }
        // Anima as vidas se elas diminuíram
        if (placar.vidas < placarAnterior.current.vidas) {
            vidasControls.start({
                x: [0, -5, 5, -5, 0], // Efeito de tremer
                transition: { duration: 0.4 }
            });
        }
        // Guarda o placar atual para a próxima comparação
        placarAnterior.current = placar;
    }, [placar, scoreControls, vidasControls]);


    useEffect(() => {
        // ... (código do useEffect do timer não muda)
    }, [modo, carregando, tempo, statusResposta.selecionada]);

    useEffect(() => {
        // ... (código do useEffect de fim de jogo do timer não muda)
    }, [modo, tempo, placar.score, voltarAoMenu]);

    const handleResposta = async (respostaDoUsuario) => {
        // ... (código da função handleResposta não muda)
    };
    
    const getButtonClass = (opcao) => {
        // ... (código da função getButtonClass não muda)
    };

    if (!pergunta && carregando) { return <div>Carregando...</div>; }
    if (!pergunta) { return <div>Não foi possível carregar o jogo. Verifique se a API está no ar.</div>; }

    return (
        <div className={theme}> 
            <div className={styles.container}>
                <div className={styles.placar}>
                    {/* Conectamos os elementos ao controle de animação */}
                    <motion.p animate={scoreControls}>
                        <b>Pontos: {placar.score}</b>
                    </motion.p>
                    {modo === 'classico' ? (
                        <motion.p animate={vidasControls}>
                            <b>Vidas: {placar.vidas} ❤️</b>
                        </motion.p>
                    ) : (
                        <p><b>Tempo: {tempo < 0 ? 0 : tempo}s ⏳</b></p>
                    )}
                </div>

                <AnimatePresence mode="wait">
                    {!carregando && pergunta && (
                        <motion.div 
                            key={pergunta.pergunta}
                            initial={{ opacity: 0, y: -20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: 20 }}
                            transition={{ duration: 0.5 }}
                            className={styles.pergunta}
                        >
                            <h2>{pergunta.pergunta}</h2>
                            {pergunta.flagUrl && ( <img src={pergunta.flagUrl} alt="Bandeira do país" className={styles.bandeira} /> )}
                            {pergunta.dica_extra && <p><em>Dica: {pergunta.dica_extra}</em></p>}
                        </motion.div>
                    )}
                </AnimatePresence>
                
                {feedback && <div className={styles.feedback}>{feedback}</div>}

                <div className={styles.opcoes}>
                    {pergunta.opcoes.map((opcao, index) => (
                        <motion.button 
                            key={index}
                            className={getButtonClass(opcao)}
                            onClick={() => handleResposta(opcao)}
                            disabled={carregando || statusResposta.selecionada}
                            // Adicionamos as animações de hover e clique
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                        >
                            {opcao}
                        </motion.button>
                    ))}
                </div>
                <button onClick={voltarAoMenu} className={styles.menuButton}>Voltar ao Menu</button>
            </div>
        </div>
    );
}

export default Jogo;
